INSERT INTO "core"."riesgos_contables" ("id", "tenant_id", "titulo", "descripcion", "categoria_niif", "nivel_confianza_ia", "explicacion_ia", "estado", "datos_relacionados", "fecha_deteccion", "validado_por_user_id", "comentarios_revisor", "fecha_validacion") VALUES ('1', 'default_tenant', 'Transacción de ingreso sin referencias de soporte', 'El registro de un ingreso significativo (3,2 millones COP) carece de referencias críticas como el ''referencia_factura_id'' y el ''cuenta_bancaria_id''. Esto dificulta la trazabilidad de la transacción, la verificación de la fuente de los fondos (IFRS 9) y la validación de la existencia de un ingreso devengado (IFRS 15). Indica una debilidad en los controles internos de registro.', 'Trazabilidad de Ingresos y Control Interno (IFRS 15, IFRS 9)', '85.00', 'El registro de un ingreso significativo (3,2 millones COP) carece de referencias críticas como el ''referencia_factura_id'' y el ''cuenta_bancaria_id''. Esto dificulta la trazabilidad de la transacción, la verificación de la fuente de los fondos (IFRS 9) y la validación de la existencia de un ingreso devengado (IFRS 15). Indica una debilidad en los controles internos de registro.', 'DETECTADO', '{"movimiento_id":11}', '2025-12-11 12:55:23.827375+00', null, null, null), ('2', 'default_tenant', 'Ingreso sin referencia de cuenta bancaria ni factura', 'El registro de ''ingreso'' de caja/mostrador carece de un ID de factura asociado y de una cuenta bancaria/caja de destino. Esto dificulta la conciliación del efectivo o la verificación del cumplimiento de NIIF 15 (Trazabilidad de ingresos).', 'Trazabilidad de Ingresos (NIIF 15)', '0.80', 'El registro de ''ingreso'' de caja/mostrador carece de un ID de factura asociado y de una cuenta bancaria/caja de destino. Esto dificulta la conciliación del efectivo o la verificación del cumplimiento de NIIF 15 (Trazabilidad de ingresos).', 'DETECTADO', '{"movimiento_id":16}', '2025-12-11 12:55:23.80054+00', null, null, null), ('3', 'default_tenant', 'Monto sospechoso y falta de documentación', 'Egreso por un monto elevado (9,999,999) con una descripción vaga ("servicios varios urgente"). Carece de referencias de factura, orden de pago y cuenta bancaria, lo que incumple los controles internos y las NIIF (IAS 1 y IAS 8) en cuanto a la documentación de transacciones.', 'Control Interno y Documentación', '0.90', 'Egreso por un monto elevado (9,999,999) con una descripción vaga ("servicios varios urgente"). Carece de referencias de factura, orden de pago y cuenta bancaria, lo que incumple los controles internos y las NIIF (IAS 1 y IAS 8) en cuanto a la documentación de transacciones.', 'DETECTADO', '{"movimiento_id":13}', '2025-12-11 12:55:23.82676+00', null, null, null), ('4', 'default_tenant', 'Egreso sin soporte documental', 'El egreso carece de referencias de factura, orden de pago y cuenta bancaria. Esto representa una debilidad crítica de control interno y riesgo de fraude por egresos ficticios.', 'Control Interno y Evidencia Documental', '0.90', 'El egreso carece de referencias de factura, orden de pago y cuenta bancaria. Esto representa una debilidad crítica de control interno y riesgo de fraude por egresos ficticios.', 'DETECTADO', '{"movimiento_id":10}', '2025-12-11 12:55:23.808586+00', null, null, null), ('5', 'default_tenant', 'Egreso sin documentación de soporte NIIF', 'El egreso de 4,500,000 COP carece de referencias clave (factura_id, orden_pago_id, cuenta_bancaria_id) y tiene una fecha futura (2025). Esta falta de documentación contraviene los principios de fiabilidad en la medición y el control interno NIIF, sugiriendo un posible fraude o error de registro. La ausencia de una factura de soporte dificulta la verificación de la obligación de pago y su reconocimiento.', 'Reconocimiento de Transacciones', '0.90', 'El egreso de 4,500,000 COP carece de referencias clave (factura_id, orden_pago_id, cuenta_bancaria_id) y tiene una fecha futura (2025). Esta falta de documentación contraviene los principios de fiabilidad en la medición y el control interno NIIF, sugiriendo un posible fraude o error de registro. La ausencia de una factura de soporte dificulta la verificación de la obligación de pago y su reconocimiento.', 'DETECTADO', '{"movimiento_id":14}', '2025-12-11 12:55:23.82747+00', null, null, null), ('6', 'default_tenant', 'Egreso sin soporte documental (Factura/Orden de Pago)', 'La transacción de egreso no tiene asociada ninguna referencia de factura (referencia_factura_id: null) ni orden de pago (referencia_orden_pago_id: null). Esto representa una deficiencia crítica en los controles internos de pago, impidiendo la validación del gasto y aumentando el riesgo de fraude o error en el registro contable, ya que no se puede verificar la legitimidad ni el monto de la obligación.', 'Control Interno y Fiabilidad de la Información (NIIF 1)', '0.90', 'La transacción de egreso no tiene asociada ninguna referencia de factura (referencia_factura_id: null) ni orden de pago (referencia_orden_pago_id: null). Esto representa una deficiencia crítica en los controles internos de pago, impidiendo la validación del gasto y aumentando el riesgo de fraude o error en el registro contable, ya que no se puede verificar la legitimidad ni el monto de la obligación.', 'DETECTADO', '{"movimiento_id":4}', '2025-12-11 12:55:23.891332+00', null, null, null), ('7', 'default_tenant', 'Egreso sin referencia de factura ni orden de pago', 'El egreso por un monto significativo (4.5M COP) carece de referencias clave como la ''referencia_factura_id'' y la ''referencia_orden_pago_id''. Esto incumple los controles internos básicos, dificultando la trazabilidad y verificación del gasto. Representa un alto riesgo de error contable o posible fraude, ya que no se puede validar la existencia de la obligación ni el destino del pago.', 'Verificabilidad y Control Interno (Marco Conceptual)', '90.00', 'El egreso por un monto significativo (4.5M COP) carece de referencias clave como la ''referencia_factura_id'' y la ''referencia_orden_pago_id''. Esto incumple los controles internos básicos, dificultando la trazabilidad y verificación del gasto. Representa un alto riesgo de error contable o posible fraude, ya que no se puede validar la existencia de la obligación ni el destino del pago.', 'DETECTADO', '{"movimiento_id":15}', '2025-12-11 12:55:23.799895+00', null, null, null), ('8', 'default_tenant', 'Falta de documentación de respaldo para inversión de socio', 'Transacción de alto valor (5,000,000 COP) descrita como "INVERSION SOCIO A" (transacción con parte relacionada). La ausencia de referencias a documentos legales como contratos de capitalización o acuerdos de préstamo (null en `referencia_factura_id` y `referencia_orden_pago_id`) dificulta la correcta clasificación NIIF entre patrimonio (IAS 32) y pasivo financiero (IFRS 9), constituyendo una debilidad de control interno y riesgo de clasificación.', 'IAS 24 / IAS 32', '4.00', 'Transacción de alto valor (5,000,000 COP) descrita como "INVERSION SOCIO A" (transacción con parte relacionada). La ausencia de referencias a documentos legales como contratos de capitalización o acuerdos de préstamo (null en `referencia_factura_id` y `referencia_orden_pago_id`) dificulta la correcta clasificación NIIF entre patrimonio (IAS 32) y pasivo financiero (IFRS 9), constituyendo una debilidad de control interno y riesgo de clasificación.', 'DETECTADO', '{"movimiento_id":5}', '2025-12-11 12:55:23.894885+00', null, null, null), ('9', 'default_tenant', 'Egreso sin soporte documental', 'El egreso carece de referencias de factura, orden de pago y cuenta bancaria. Esto representa una debilidad crítica de control interno y riesgo de fraude por egresos ficticios.', 'Control Interno y Evidencia Documental', '0.90', 'El egreso carece de referencias de factura, orden de pago y cuenta bancaria. Esto representa una debilidad crítica de control interno y riesgo de fraude por egresos ficticios.', 'DETECTADO', '{"movimiento_id":10}', '2025-12-11 1